$(document).ready(function(){$("div[data-section]").click(function(){var a=$(this).attr("data-section");$("div[data-section]").not('[data-section="'+a+'"]').removeAttr("class");if($.cookie("open_tabs")!==undefined&&a==$.cookie("open_tabs")){$.removeCookie("open_tabs",{path:"/cab/"})}else{$.cookie("open_tabs",a,{expires:14,path:"/cab/"})}if($(this).is(".active")){$(this).removeAttr("class")}else{$(this).addClass("active")}$("div[data-view-section]").each(function(){if($(this).attr("data-view-section")!=a){$(this).slideUp()}else{if($(this).css("display")=="block"){$(this).slideUp()}else{$(this).slideDown()}}})})});